          frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
        </div>
      `;
            modal.classList.add('modal-open'); // Use DaisyUI's class to open the modal
        }

        function closeVideoModal() {
            const modal = document.getElementById('videoModal');
            // Clear the innerHTML of the modal box to stop the video
            modal.querySelector('.modal-box').innerHTML = '';
            modal.classList.remove('modal-open'); // Use DaisyUI's class to close the modal
        }
    </script>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const hasLoadedBefore = sessionStorage.getItem('hasLoadedBefore');
            const loadingScreen = document.getElementById('loadingScreen');
            const mainContent = document.getElementById('mainContent');

            if (hasLoadedBefore) {
                // Hide loading screen and show main content immediately
                if (loadingScreen) loadingScreen.style.display = 'none';
                mainContent.style.opacity = 1;
                mainContent.style.visibility = 'visible';
            } else {
                // First time loading, show the loading screen then main content
                setTimeout(() => {
                    // Start hiding the loading screen
                    loadingScreen.style.opacity = '0';
                    loadingScreen.style.visibility = 'hidden';

                    setTimeout(() => {
                        // Remove loading screen and show main content after fade out
                        loadingScreen.style.display = 'none';
                        mainContent.style.opacity = 1;
                        mainContent.style.visibility = 'visible';
                    }, 500); // Matches the CSS transition time
                }, 3000); // Adjust as needed for your desired loading screen display time

                // Mark that the site has loaded before
                sessionStorage.setItem('hasLoadedBefore', 'true');
            }
        });
    </script>

